import { useEffect, useState } from 'react';
import '../App.css';
import { Font } from './FontButton';
import { getPng } from '../utils/EmojiRenderer';

/** ResultBoxプロパティ */
type ResultBoxProps = {
  text: string,
  font: Font,
  color: string,
}

const ResultBox = ({ text, font, color }: ResultBoxProps) => {
  /** 結果png */
  const [png, setPng] = useState<string | null>(null)

  useEffect(() => {
    (async () => {
      const png = await getPng(text, color, font);
      setPng(png);
    })();
  }, [text, font, color])

  return (
    <>
      <div>
        <span className='reault'>
          {png && (
            <div className="comp" style={{ display: 'flex' }}>
              <img alt="result" src={png} />
            </div>
          )}
        </span>
      </div>
    </>
  );
}

export default ResultBox;
