import { useEffect, useState } from 'react';
import './App.css';

type EmojiPreviewBoxProps = {
  text: string,
  font: {
    displayName: string,
    name: string,
  }
  color: string,
}
const EmojiPreviewBox = ({ text, font, color }: EmojiPreviewBoxProps) => {
  useEffect(() => {
    init();
  });

  const [textLine] = useState(text.split('\n'));

  /**
   * 初期処理
   */
  const init = () => {
    if (textLine.some((line) => line.length > Number(process.env.REACT_APP_MAX_LETTERS_AT_LINE))) {
      console.log("文字数超過テスト")
    };
  }

  return (
    <>
      <span style={{ fontFamily: font.name }} className='font-preview'>
        {
          textLine.map((line) => {
            return <div className='font-preview-content' style={{ color: color }}>{line}</div>
          })
        }
        <div className='font-preview-content' style={{ color: color }}>{text}</div>
      </span>
    </>
  );
}

export default EmojiPreviewBox;
