import React from 'react';
import './App.css';
import EmojiPreviewBox from './EmojiPreviewBox';

type FontButtonProps = {
  text: string,
  font: {
    displayName: string,
    name: string,
  }
  pressed: boolean,
  onClick: React.MouseEventHandler<HTMLButtonElement>
}

const FontButton = ({ text, font, pressed, onClick }: FontButtonProps) => {
  return (
    <>
      <div>
        <span className='font'>
          <button name={font.name} value={font.name} onClick={onClick} disabled={pressed} className='font-button'>
            <div style={{fontFamily: font.name}} className='font-preview'>
              <div className='font-preview-content'>{text}</div>
              </div>
              <EmojiPreviewBox text={text} font={font} color='#000000'/>
            <div className='font-name'>{font.displayName}</div>
          </button>
        </span>
      </div>
    </>
  );
}

export default FontButton;
