import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

type Font = {
  name: string,
  style: string,
}
type FontProps = {
  fonts: Font[];
}

  /**
   * フォント名セレクタ
   */
  const [font, setFont] = useState<Font>({
    name: '',
    style :'',
  });

// セレクトボックスの値が変更された際に実行される
const handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
  setFont((f) => ({...f, [e.target.name]:e.target.value}));
};

class FontSelect extends React.Component<FontProps, {}> {
  render() {
    return (
      <div >
        <div className="font">
          <select name='font-name' onChange={handleChange}>
            {
              this.props.fonts.map((f) => {
                return (<option value={f.name}>{f.name}</option>)
              })
            }
          </select>
        </div>
      </div>
    );
  }
}

class Main extends React.Component {
  render() {
    return (
      <div className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </div>
    );
  }
}

const App: React.FC = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
