import { useEffect, useState } from 'react';
import '../App.css';
import { Font } from '../components/FontButton';

export class EmojiRenderer {
  /**
   * プレビュー用canvas描画
   */
  async getPng(text: string, color: string, font: Font): Promise<string> {
    const [textLine, setTextLine] = useState(text.split('\n'));
    return new Promise((resolve, reject) => {
      try {
        const fontSize = 32;
        const canvas = document.createElement('canvas');
        canvas.width = 128;
        canvas.height = 128;
        const ctx = canvas.getContext('2d')!;
        ctx.fillStyle = color;
        ctx.font = `${fontSize}px '${font.name}', serif`;

        // draw
        const textLine = text.split('\n');
        textLine.forEach((line, i) => {
          ctx.save();
          const lineWidth = ctx.measureText(line).width;
          ctx.scale(canvas.width / lineWidth, 4 / textLine.length);
          ctx.fillText(line, 0, (fontSize - 4) * (i + 1));
          ctx.restore();
        });
        resolve(canvas.toDataURL());
      } catch (e) {
        reject();
      }
    });
  }
}
