import React, { useState } from 'react';

type Font = {
  name: string,
  style: string[],
};

type Value = {
  name: string,
  style: string,
}

type FontProps = Font[];

/**
 * フォントselect要素セット
 */
const FontSelector = ({props, value}: {props: FontProps, value: React.Dispatch<React.SetStateAction<Value>>}): JSX.Element => {
  // フォント
  const [fontIndex, setFontIndex] = useState<number>(0);
  const [styleIndex, setStyleIndex] = useState<number>(0);

  /**
   * フォントselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleFontChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const index = Number(e.target.value);
    setFontIndex(index);
    setStyleIndex(0);
    value({
      name: props[index].name,
      style: props[index].style[0],
    });
  };

  /**
   * スタイルselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleStyleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const index = Number(e.target.value)
    setStyleIndex(index);
    value((v) =>
        ({
          ...v,
          style: props[fontIndex].style[index],
        })
    );
  };

  return (
    <div >
      <span className='font'>
        <select name='name' value={fontIndex} onChange={handleFontChange}>
          {
            props.map((font, i) => {
              return (<option value={i}>{font.name}</option>)
            })
          }
        </select>
      </span>
      &nbsp;
      <span className='style'>
        <select name='style' value={styleIndex} onChange={handleStyleChange}>
          {
            props[fontIndex].style.map((style, i) => {
              return (<option value={i}>{style}</option>)
            })
          }
        </select>
      </span>
    </div>
  );
}

export default FontSelector;
