import React, { useState } from 'react';
import ReactDOMServer from "react-dom/server";
import Fonts from './resource/fonts.json'
import ConvertList from './resource/letter_convert_list.json'
import './App.css';
import FontButton from './FontButton';
import EmojiPreviewBox from './EmojiPreviewBox';

/**
 * 絵文字名変換表
 */
const convertList: { [key: string]: string } = ConvertList;

const Main = () => {
  /** 絵文字にする文字列 */
  const [text, setText] = useState('');

/**
 * 選択中のフォント名
 */
  const [selectedFont, setSelectedFont] = useState({
    displayName: '',
    name: '',
  });

  /**
   * 最大級サーバー向け文字変換関数
   */
  const createEmojiName = () => {
    let convertedText = text;

    // 変換表に基づいて変換
    Object.keys(convertList).forEach((letter: string) => {
      convertedText.replaceAll(letter, convertList[letter]);
    });

    // 長音記号は前の文字と一致させる
    let i = 0;
    while (convertedText.indexOf("ー", i) > -1) {
      const preLetter = convertedText[convertedText.indexOf("ー", i) - 1];
      console.log(preLetter);
      convertedText = convertedText.replace("ー", preLetter);
      i++;
    }
  }

  const createForeignObject = (width: number, height: number, content: string) => {
    const g = document.createElementNS("http://www.w3.org/2000/svg", "g");
    const sw = document.createElementNS("http://www.w3.org/2000/svg", "switch");

    // foreignObjectを作成
    const foreignObject = document.createElementNS(
      "http://www.w3.org/2000/svg",
      "foreignObject"
    );
    foreignObject.setAttribute("width", width.toString());
    foreignObject.setAttribute("height", height.toString());
    foreignObject.setAttribute(
      "requiredExtensions",
      "http://www.w3.org/1999/xhtml"
    );

    const div = document.createElementNS("http://www.w3.org/1999/xhtml", "div");
    div.setAttribute("xmlns", "http://www.w3.org/1999/xhtml");
    div.innerHTML = content;

    // foreignObjectがサポートされてない場合に以下のtextが表示される
    const fallthrough = document.createElementNS(
      "http://www.w3.org/2000/svg",
      "text"
    );
    fallthrough.setAttribute("x", "0");
    fallthrough.setAttribute("y", "10");
    fallthrough.setAttribute("font-size", "10");
    fallthrough.setAttribute("fill", "red");
    fallthrough.textContent = "failed to generate svg";

    foreignObject.appendChild(div);
    sw.appendChild(foreignObject);
    sw.appendChild(fallthrough);
    g.appendChild(sw);
    return g;
  };

  /**
   * テキストエリア要素変更イベント
   * @param e textarea要素変更イベント
   */
  const handleTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setText(e.target.value);
  };

  const element = useRef<HTMLDivElement>(null);
  return (
    <div className="App-header">
            <button
        onClick={() => {
          const svg = document.createElementNS(
            "http://www.w3.org/2000/svg",
            "svg"
          );
          svg.setAttribute("width", "100%");
          svg.setAttribute("height", "100%");
          const content = ReactDOMServer.renderToStaticMarkup(<Item />);
          const g = createForeignObject(500, 500, content);
          svg.appendChild(g);
          element.current && element.current.appendChild(svg);
        }}
      >
        Convert
      </button>

      <div>
        {/* テキスト */}
        <textarea value={text} style={{ fontFamily: selectedFont.name }} onChange={handleTextChange} />
      </div>
      <div>
        <span className='font'>
          {
            /* フォントボタン */
            Fonts.map((font) => {
              return <FontButton key={font.name} text={text} font={font} pressed={selectedFont.name === font.name} onClick={() => { setSelectedFont(font); }} />
            })
          }
        </span>
      </div>
    </div>
  );
}

const App = () => {
  return (
    <>
      {/* フォント読み込み */}
      <link rel="preconnect" href="https://fonts.googleapis.com" />
      <link href={`https://fonts.googleapis.com/css2?${Fonts.map((font) => 'family=' + font.name.replaceAll(' ', '+')).join('&')}&display=swap`} rel="stylesheet" />
      <div className="main">
        <Main />
      </div>
    </>
  );
}

export default App;
