import { useEffect, useState } from 'react';
import '../App.css';
import { Font } from '../components/FontButton';

/**
 * プレビュー用canvas描画
 */
export async function getPng(text: string, font: Font, color: string, borderColor: string, borderWidth: number): Promise<string> {
  return new Promise((resolve, reject) => {
    try {
      const canvas = document.createElement('canvas');
      canvas.width = 128;
      canvas.height = 128;

      const textLine = text.split('\n');
      const fontSize = canvas.width / textLine.length;

      const ctx = canvas.getContext('2d')!;
      ctx.fillStyle = color;
      ctx.strokeStyle = borderColor;
      ctx.lineWidth = borderWidth;
      ctx.lineJoin = 'round';
      ctx.textBaseline = 'top';
      ctx.font = `${fontSize}px '${font.name}', serif`;

      // stroke
      if (borderWidth > 0) {
        textLine.forEach((line, i) => {
          ctx.save();
          //const lineWidth = ctx.measureText(line).width;
          ctx.strokeText(line, canvas.width * 0.05, (fontSize * 0.9 * i) + fontSize * 0.05, canvas.width * 0.9);
          ctx.restore();
        });
      }

      // fill
      textLine.forEach((line, i) => {
        ctx.save();
        ctx.fillText(line, canvas.width * 0.05, (fontSize * 0.92 * i) + fontSize * 0.05, canvas.width * 0.9);
        ctx.restore();
      });
      resolve(canvas.toDataURL());
    } catch (e) {
      reject();
    }
  });
}

function getXOffset(canvas: HTMLCanvasElement) {
  const ctx = canvas.getContext('2d')!;
  const data = ctx.getImageData(0, 0, canvas.width, canvas.height).data;

  for (var i=0; i<data.length; i+=4) {
    // データ4つでrgba 1px分
    if (data[i] !== 0 || data[i+1] !== 0 || data[i+2] !== 0 || data[i+3] !== 0) {
      break;
    }
  }

  // "pixel" position p in data
  let p;

  // find x offset (bottom row of pixels)
  let from = data.length - 32 * 4;

  // increase by 4 to step through the data per pixel instead of per color value
  for (p = from; p < data.length; p += 4) {
    // stop when we find a value other than 0
    if (data[p]) break;
  }

  // calculate the x offset based on the current pixel
  const x = (p - from) / 4;

  // find y offset (right column of pixels)
  from = 31 * 4;
  for (p = from; p < data.length; p += 32 * 4) {
    if (data[p]) break;
  }

  // calculate the y offset based on the current pixel
  const y = (p - from) / (32 * 4);

  // log the position to the console (it's there, go take a look)
  console.log(x, y);
}
