import React, { useEffect, useState } from 'react';
import Fonts from './fonts.json'
import './App.css';

/**
 * 読み込むフォント
 */
const fontList = Fonts;

const Main = () => {
  /** 絵文字にする文字列 */
  const [text, setText] = useState('');

  /** フォント名 */
  const [fontName, setFontName] = useState(fontList[0] !== undefined ? fontList[0].name : '');

  useEffect(() => {

  }, [fontName]);

  /**
   * テキストエリア要素変更イベント
   * @param e textarea要素変更イベント
   */
  const handleTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setText(e.target.value);
  };

  return (
    <div className="App-header">
      <div>
        {/* テキスト */}
        <textarea value={text} onChange={handleTextChange} />
      </div>
      <div>
        <span className='font'>
          {
            /* フォント名 */
            Fonts.map((font) => {
              return <button name={font.name} value={font.name} style={{fontFamily: font.name}} onClick={() => { setFontName(font.name) }}>{font.name}</button>
            })
          }
        </span>
      </div>
    </div>
  );
}

const App = () => {
  return (
    <>
      <link rel="preconnect" href="https://fonts.googleapis.com" />
      <link href="https://fonts.googleapis.com/css2?family=Hachi+Maru+Pop&family=Noto+Sans+JP:wght@100..900&display=swap" rel="stylesheet" />
      <div className="main">
        <Main />
      </div>
    </>
  );
}

export default App;
