import React, { useEffect, useState } from 'react';
import Fonts from './fonts.json'
import './App.css';

/**
 * 読み込むフォント
 */
const fontList = Fonts;

const Main = () => {
  type Font = {
    name: string,
    style: string[],
  };

  // フォント
  const [fontIndex, setFontIndex] = useState(0);
  const [styleIndex, setStyleIndex] = useState(0);

  /**
   * フォントselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleFontChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const index = Number(e.target.value);
    setFontIndex(index);
    setStyleIndex(0);
  };

  /**
   * スタイルselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleStyleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const index = Number(e.target.value)
    setStyleIndex(index);
  };



  const [font, setFont] = useState({
    name: '',
    style: '',
  });

  const [text] = useState('');

  useEffect(() => {
    console.log(font);
  }, [font]);

  return (
    <div className="App-header">
      <div>
      <textarea value={text} />
      </div>
      <div>
      <span className='font'>
        <select name='name' value={fontIndex} onChange={handleFontChange}>
          {
            fonts.map((font, i) => {
              return (<option value={i}>{font.name}</option>)
            })
          }
        </select>
      </span>
      &nbsp;
      <span className='style'>
        <select name='style' value={styleIndex} onChange={handleStyleChange}>
          {
            fonts[fontIndex].style.map((style, i) => {
              return (<option value={i}>{style}</option>)
            })
          }
        </select>
      </span>
    </div>
    </div>
  );
}

const App = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
