import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

type Font = {
  name: string,
  style: string[],
};

type FontProps = Font[];

/**
 * フォント名select要素
 */
const FontSelect = ({ props }: { props: FontProps }) => {
  /**
    * フォント
    */
  const [font, setFont] = useState({
    name: '',
    style: '',
  });

  /**
   * select要素変更イベント
   * @param e select要素変更イベント
   */
  const handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
