  };

  return (
    <div >
      <div className='font'>
        <select name='name' onChange={handleChange}>
          {
            props.map((f, i) => {
              return (<option value={i}>{f.name}</option>)
            })
          }
        </select>
      </div>
      <div className='style'>
        <select name='style' onChange={handleChange}>
          {
            props.map((f, i) => {
              return (<option value={i}>{f.style}</option>)
            })
          }
        </select>
      </div>
    </div>
  );
}

const fonts = [
  {
    name: 'ヒラギノ角ゴ ProN',
    style: ['W3'],
  }
];

const Main = () => {
  return (
    <div className="App-header">
      <FontSelect props={fonts} />
    </div>
  );
}

const App: React.FC = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
