import React, { useEffect, useState } from 'react';
import Fonts from './fonts.json'
import './App.css';

/**
 * 読み込むフォント
 */
const fontList = Fonts;

const Main = () => {
  const [font, setFont] = useState({
    text: '',
    name: '',
    style: [] as string[],
  });

/** フォントスタイル */
  const [style, setStyle] = useState('');

  /** フォントスタイル候補 */
  const [styleList, setStyleList] = useState([] as string[]);

  /**
   * フォントselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleFontChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setFont((f) => ({
      ...f,
      name: e.target.value,
    }));
  };

  /**
   * スタイルselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleStyleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const index = Number(e.target.value)
    setStyleIndex(index);
  };

  return (
    <div className="App-header">
      <div>
        {/* テキスト */}
        <textarea value={font.text} />
      </div>
      <div>
      <span className='font'>
        {/* フォント名 */}
        <select name='name' value={font.name} onChange={handleFontChange}>
          {
            fontList.map((f, i) => {
              return (<option>{f.name}</option>)
            })
          }
        </select>
      &nbsp;
      {/* フォントスタイル */}
        <select name='style' value={font.style} onChange={handleStyleChange}>
          {
            (fontList.filter((f) => {f.name == font.name}) ?? []).map((f, i) => {
              return (<option>{f.style[i]}</option>)
            })
          }
        </select>
      </span>
    </div>
    </div>
  );
}

const App = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
