import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

type Font = {
  name: string,
  style: string,
};

type FontProps = Font[];

/**
 * フォント名セレクタ
 */
const [font, setFont] = useState<Font>({
  name: '',
  style: '',
});

/**
 * select要素変更イベント
 * @param e select要素変更イベント
 */
const handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
  setFont((f) => ({ ...f, [e.target.name]: e.target.value }));
};

/**
 * フォント名select要素
 */
const FontSelect = (props: FontProps) => {
  return (
    <div >
      <div className='font'>
        <select name='name' onChange={handleChange}>
          {
            props.map((f) => {
              return (<option value={f.name}>{f.name}</option>)
            })
          }
        </select>
      </div>
      <div className='style'>
        <select name='name' onChange={handleChange}>
          {
            props.map((f) => {
              return (<option value={f.style}>{f.style}</option>)
            })
          }
        </select>
      </div>
    </div>
  );
}

const fonts = [
  {
    name: '',
    style: '',
  }
];

const Main = () => {
  return (
    <div className="App-header">
      <FontSelect props={fonts} />
    </div>
  );
}

const App: React.FC = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
