import React, { useEffect, useState } from 'react';
import Fonts from './fonts.json'
import './App.css';

/**
 * 読み込むフォント
 */
const fontList = Fonts;

const Main = () => {
  /** 絵文字にする文字列 */
  const [text, setText] = useState('');

  /** フォント名 */
  const [name, setName] = useState('');

  /** フォントスタイル */
  const [style, setStyle] = useState('');

  /** フォントスタイル候補 */
  const [styleList, setStyleList] = useState([] as string[]);

  /**
   * フォントselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleFontChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setName(e.target.value);
  };

  useEffect(() => {
    const styleList = (fontList.find((f) => { f.name === name }) ?? {style: []}).style;
    setStyleList(styleList);
    setStyle(styleList[0] ?? '');
  }, [name]);

  /**
   * スタイルselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleStyleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setStyle(e.target.value);
  };

  /**
   * テキストエリア要素変更イベント
   * @param e textarea要素変更イベント
   */
    const handleTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
      setText(e.target.value);
    };

  return (
    <div className="App-header">
      <div>
        {/* テキスト */}
        <textarea value={text} onChange={handleTextChange}/>
      </div>
      <div>
        <span className='font'>
          {/* フォント名 */}
          <select name='name' value={name} onChange={handleFontChange}>
            {
              fontList.map((f, i) => {
                return (<option>{f.name}</option>)
              })
            }
          </select>
          &nbsp;
          {/* フォントスタイル */}
          <select name='style' value={style} onChange={handleStyleChange}>
            {
              (fontList.filter((f) => { f.name == name }) ?? []).map((f, i) => {
                return (<option>{f.style[i]}</option>)
              })
            }
          </select>
        </span>
      </div>
    </div>
  );
}

const App = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
