import { useEffect, useState } from 'react';
import '../App.css';
import { Font } from '../components/FontButton';

/**
 * プレビュー用canvas描画
 */
export async function getPng(text: string, font: Font, color: string, borderColor: string, borderWidth: number): Promise<string> {
  return new Promise((resolve, reject) => {
    try {
      const fontSize = 34;
      const canvas = document.createElement('canvas');
      canvas.width = 128;
      canvas.height = 128;
      canvas.style.letterSpacing = '50px';
      const ctx = canvas.getContext('2d')!;
      ctx.fillStyle = color;
      ctx.strokeStyle = borderColor;
      ctx.lineWidth = borderWidth;
      ctx.lineJoin = 'round';
      ctx.font = `${fontSize}px '${font.name}', serif`;
      const textLine = text.split('\n');

      // stroke
      if (borderWidth > 0) {
        textLine.forEach((line, i) => {
          ctx.save();
          const lineWidth = ctx.measureText(line).width;
          // ctx.scale(0.9 * canvas.width / lineWidth, 0.9 * canvas.height / (fontSize * textLine.length));
          ctx.strokeText(line, 0, (fontSize * 0.9) * (i + 1), canvas.width);
          ctx.restore();
        });
      }

      // fill
      textLine.forEach((line, i) => {
        ctx.save();
        const lineWidth = ctx.measureText(line).width;
        ctx.scale(0.9 * canvas.width / lineWidth, 0.9 * canvas.height / (fontSize * textLine.length));
        ctx.fillText(line, 0, (fontSize * 0.9) * (i + 1));
        ctx.restore();
      });
      resolve(canvas.toDataURL());
    } catch (e) {
      reject();
    }
  });
}
