import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

type Font = {
  name: string,
  style: string[],
};

type FontProps = Font[];

/**
 * フォント名select要素
 */
const FontSelect = ({ props }: { props: FontProps }) => {
  // フォント
  const [fontIndex, setFontIndex] = useState<number>(0);
  const [styleIndex, setStyleIndex] = useState<number>(0);

  /**
   * フォントselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleFontChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setFontIndex(Number(e.target.value));
    setStyleIndex(0);
  };

  /**
   * スタイルselect要素変更イベント
   * @param e select要素変更イベント
   */
  const handleStyleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setStyleIndex(Number(e.target.value));
  };

  useEffect(() => {

  }, [fontIndex]);

  return (
    <div >
      <div className='font'>
        <select name='name' value={fontIndex} onChange={handleFontChange}>
          {
            props.map((font, i) => {
              return (<option value={i}>{font.name}</option>)
            })
          }
        </select>
      </div>
      <div className='style'>
        <select name='style' value={styleIndex} onChange={handleStyleChange}>
          {
            props[fontIndex].style.map((style, i) => {
              return (<option value={i}>{style}</option>)
            })
          }
        </select>
      </div>
    </div>
  );


}




const fonts = [
  {
    name: 'ヒラギノ角ゴ ProN',
    style: ['W3', 'W6'],
  },
  {
    name: 'ヒラギノ角ゴ Pro',
    style: ['W3'],
  }
];

const Main = () => {
  return (
    <div className="App-header">
      <FontSelect props={fonts} />
    </div>
  );
}

const App: React.FC = () => {
  return (
    <div className="main">
      <Main />
    </div>
  );
}

export default App;
