import { useEffect, useState } from 'react';
import '../App.css';
import { Font } from '../components/FontButton';

/**
 * プレビュー用canvas描画
 */
export async function getPng(text: string, font: Font, color: string, borderColor: string, borderWidth: number): Promise<string> {
  return new Promise((resolve, reject) => {
    try {      
      const fontSize = 34;
      const canvas = document.createElement('canvas');
      canvas.width = 128;
      canvas.height = 128;
      const ctx = canvas.getContext('2d')!;
      ctx.fillStyle = color;
      ctx.strokeStyle = borderColor
      ctx.lineWidth = borderWidth;
      ctx.font = `${fontSize}px '${font.name}', serif`;

      // draw
      const textLine = text.split('\n');
      textLine.forEach((line, i) => {
        ctx.save();
        const lineWidth = ctx.measureText(line).width;
        ctx.scale(canvas.width / lineWidth, canvas.height / (fontSize * textLine.length));
        ctx.fillText(line, 0, fontSize * (i + 1) - 3);
        ctx.restore();
      });
      resolve(canvas.toDataURL());
    } catch (e) {
      reject();
    }
  });
}
